mapper.update:
update orders
cancel_reason=#{cancelReason}, rejection_reason=#{rejectionReason}, cancel_time=#{cancelTime}, pay_status=#{payStatus}, pay_method=#{payMethod}, checkout_time=#{checkoutTime}, status = #{status}, delivery_time = #{deliveryTime}
where id = #{id}

mapper.select:
select * from orders


        and number = #{number}


        and status = #{status}


        and user_id = #{userId}


and phone = #{phone}


order by order_time desc

select sum(amount) from orders


        and status = #{status}


        and order_time >= #{begin}


        and order_time <= #{end}

select count(*) from user


        and create_time >= #{startTime}


        and create_time <= #{endTime}

select count(*) from orders


        and status = #{status}


        and order_time >= #{startTime}


        and order_time <= #{endTime}

SELECT od.name,SUM(od.number) as number
FROM order_detail od
JOIN orders o ON od.order_id = o.id


        AND o.status = #{status}


        AND o.order_time >= #{startTime}


        AND o.order_time <= #{endTime}


GROUP BY od.name
ORDER BY COUNT(od.name) DESC
LIMIT 10
